<?php

use Illuminate\Database\Seeder;
use ViKon\Utilities\SeederProgressBarTrait;
use App\Models\Permission;

/**
 * Generated by vi-kon/db-exporter
 *
 * @author KovÃ¡cs Vince<vincekovacs@hotmail.com>
 */
class PermissionsSeeder extends Seeder {
    use SeederProgressBarTrait;

    protected $output;

    protected $structure = array (
  0 => 'id',
  1 => 'name',
  2 => 'display_name',
  3 => 'system',
  4 => 'created_at',
  5 => 'updated_at',
);

	/**
	 * Run the database seeds.
	 *
	 * @return void
	 */
	public function run()
	{
	    DB::table('permissions')->delete();

        $this->output = $this->command->getOutput();

        $progress = $this->createProgressBar();
        $this->command->line('<info>Inserting data:</info> permissions');
        $progress->start(15);

        $data = include(__DIR__ . '/data/permissions_table_data.php');
	    foreach($data as $row) {
	        $this->create($row);
	        $progress->advance();
	    }

        $progress->finish();
        $this->command->getOutput()->writeln('');
	}

	protected function create($values) {
	    $data = [];
        foreach ($this->structure as $i => $key) {
            $data[$key] = $values[$i];
        }
	    Permission::create($data);
	}

}
